import React, { useState } from "react";
import { Card, Button } from "react-bootstrap";
import { addDoc, collection } from "firebase/firestore";
import { db, auth } from "../firebase";
import { useHistory } from "react-router-dom";
import { useAuth } from '../contexts/AuthContext'

    function Task() {
  const [title, setTitle] = useState("");
  const [text, setText] = useState("");
  const [error, setError] = useState("")
  const {logout} = useAuth()

  const taskCollectionRef = collection(db, "tasks");
  const history = useHistory()

   function cancelTask(){
    setError("")
    try {
      
      history.pushState("/dashboard")
    } catch {
      setError("Falha em sair")
      
    }
  }

  const createTask = async () => {
    await addDoc(taskCollectionRef, {
      title,
      text,
      author: {
        name: auth.currentUser.email,
        id: auth.currentUser.uid,
      },
    });
    history.push("/")
  };

  return (
    <>
    <Card>
      <Card.Body>
        <Card.Title className="d-flex fw-normal mb-3">
          <input
            placeholder="Nome da task:"
            onChange={(event) => {
              setTitle(event.target.value);
            }}
          ></input>
        </Card.Title>
        <Card.Text className="d-flex fw-normal mb-3">
          <input
            placeholder="Descrição da task:"
            onChange={(event) => {
              setText(event.target.value);
            }}
          ></input>
        </Card.Text>
      </Card.Body>
      <Button onClick={createTask}>Criar</Button>
      
    </Card>
    <div className='w-100 text-center mt-2'>
    <Button style={{ textDecoration: 'none' }} variant='link' onClick={cancelTask}>
    Sair
  </Button>
  </div>
  </>
    
  );
}
export default Task
