import React, { useState } from 'react'
import { Card, Button } from 'react-bootstrap'
import {addDoc, collection} from 'firebase/firestore'
import { db, auth } from '../firebase'

export default function Task() {
  const [title, setTitle] = useState("") 
  const [text, setText] = useState("")  

  const taskCollectionRef = collection(db, "tasks")

  const createTask = async () =>{
      await addDoc(taskCollectionRef, {title, text, 
    author: {
        name: auth.currentUser.email,
        id: auth.currentUser.uid}
    })
  }


  return (
    <Card>
        <Card.Body>
            <Card.Title className='d-flex fw-normal mb-3'>
                <input placeholder='Nome da task:'
                 onChange={(event) =>{
                     setTitle(event.target.value)
                     }}></input>
            </Card.Title>
            <Card.Text className='d-flex fw-normal mb-3'>
            <input placeholder='Descrição da task:'
                 onChange={(event) =>{
                     setText(event.target.value)
                     }}></input>
            </Card.Text>
        </Card.Body>
        <Button onClick={createTask}>Criar</Button>

    </Card>
  )
}
