import React, {useRef, useState} from 'react'
import{ Form, Button, Card, Alert} from 'react-bootstrap'
import { Link } from 'react-router-dom'
import { useAuth } from '../contexts/AuthContext'


export default function Login() {
    const emailRef = useRef()
    const passwordRef = useRef()
    const {signup, currentUser} = useAuth()
    const [error, setError] = useState('')
    const [loading, setLoading] = useState(false)

    async function handleSubmit(e){
        e.preventDefault()

            try{
                setError('')
                setLoading(true)
               await signup(emailRef.current.value, passwordRef.current.value)
            }catch{
                setError('Falha em criar conta')
            }
            setLoading(false)

        
    }

  return (
    <>
    <Card>
        <Card.Body>
            <h1 className='text-center mb-3'>GPG</h1>
            <h2 className='text-center mb-4'>Entre com sua conta</h2>
            {currentUser.email}
            {error && <Alert variant='danger'>{error}</Alert>}
            <Form onSubmit={handleSubmit}>
                <Form.Group id='email'>
                    <Form.Label>Email</Form.Label>
                    <Form.Control type='email' ref={emailRef} required />
                </Form.Group>

                <Form.Group id='password'>
                    <Form.Label>Senha</Form.Label>
                    <Form.Control type='password' ref={passwordRef} required />
                </Form.Group>

                
              <Button disabled={loading} className='w-100 mt-4' type='submit' >Entrar</Button>

            </Form>
        </Card.Body>
    </Card>
    <div className='w-100 text-center mt-2'>
        NÃ£o possui uma conta? <Link to="/signup">Cadastre-se</Link>
    </div>

    </>
  )
}
